import React, { Component } from "react";
import { connect } from "react-redux";

const formAppli = [
    {
      name: "username",
      type: "text",
      label: "Nome:",
      required: false,
      pattern: "[A-Za-z]{3,}"
    },
    {
      name: "age",
      type: "number",
      label: "Idade: ",
      required: true,
      minValue: 18,
    },
    {
      name: "aplicationText",
      type: "text",
      label: "Sou bom candidato por: ",
      required: true,
      minValue: 30
    },
    {
        name: "profession",
        type: "text",
        label: "Profissão",
        required: true,
        minValue: 10
    },
    {
        name: "country",
        type: "text",
        label: "País",
        required: true,
    },
  ];

class ApplicationForm extends Component {
    constructor(props){
        super(props);
        this.state = {
            form:{}
        }
    }

render() {
    return (
        <form onSubmit={this.handleOnSubmit}>
            {loginForm.map(input => (
            <div key={input.name}>
            <label htmlFor={input.name}>{input.label}: </label>
            <input
            id={input.name}
            name={input.name}
            type={input.type}
            value={this.state.form[input.name] || ""}
            required={input.required}
            onChange={this.handleInputChange}
            pattern={input.pattern}
            />
           </div>
            ))}
        <button type="submit">Enviar</button>
        </form>
        );
    }
}
  
  
 
  export default connect(null, null)(ApplicationForm);
