import React, { Component } from "react";
import { connect } from "react-redux";

const loginForm = [
    {
      name: "username",
      type: "text",
      label: "Seu nome",
      required: false
    },
    {
      name: "email",
      type: "email",
      label: "E-mail",
      required: true
    },
    {
      name: "password",
      type: "password",
      label: "Senha",
      required: true
      
    }
  ];

class ApplicationForm extends Component {
    constructor(props){
        super(props);
        this.state = {
            form:{}
        }
    }

render() {
    return (
        <form onSubmit={this.handleOnSubmit}>
            {loginForm.map(input => (
            <div key={input.name}>
            <label htmlFor={input.name}>{input.label}: </label>
            <input
            id={input.name}
            name={input.name}
            type={input.type}
            value={this.state.form[input.name] || ""}
            required={input.required}
            onChange={this.handleInputChange}
            pattern={input.pattern}
            />
           </div>
            ))}
        <button type="submit">Enviar</button>
        </form>
        );
    }
}
  
  
 
  export default connect(null, null)(ApplicationForm);
