import React from 'react'
import './Post.css'

class Post extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            like: false,
            numeroLikes: 0,
            numeroComentarios: 0,
        }

    }

    clicarNoLike = () => {
        if (this.state.like){
            this.setState({ linkenumeroLikes: this.state.numeroLikes -1})
        }else{
            this.setstates({numeroLikes: this.state.numeroLikes + 1})
        }
    }



render(){
    return (
        <div>
            <p>future4.br</p>
            <img src="https://picsum.photos/id/5/200/200" />
            <button onChange={this.clicarNoLike} ><img src={require('../../Components/Icones/favorite.png')} /></button>
            <p>{this.state.numeroLikes}</p>
        </div>

    )

}
}

export default Post;
